df %>% select(STATE,AADR,YEAR,CAUSE_NAME) %>% filter(CAUSE_NAME == "Suicide",YEAR=="2013") %>%  ggplot(aes(x=reorder(STATE,AADR),y=AADR, fill=STATE)) + geom_bar(stat = "identity") + coord_flip() + theme(legend.position="none") + labs(x = "State",y = "Age-Adjusted Death Ratio per 100,000 People",title = "Suicide Rates by State in 2013") + theme(axis.text.y=element_text(size = 5))